simple/package.json:
{
  "name": "yarn-scaffold",
  "version": "0.6.4",
  "description": "yarn scaffold for a new site.",
  "author": "Harry Wolff <hello@hswolff.com> (http://hswolff.com/)",
  "main": "./scaffold.js",
  "license": "MIT",
  "repository": {
    "type": "git",
    "url": "git://github.com/yarnjs/yarn-scaffold.git"
  },
  "dependencies": {
    "yarn-excerpt": "^0.6.0",
    "yarn-html-minifier": "^0.6.0",
    "yarn-theme-thread": "^0.3.0"
  }
}

simple/_themes/one/templates/_loop.html:
{% for post in pagination.files %}
<article class="post">
  <header class="post-header">
    <h2 class="post-title">
      <a href="{{post.url}}">{{post.title}}</a>
    </h2>
  </header>
  <section class="post-excerpt">
    {{post.excerpt}}
    {#<p>{{post.excerpt}} <a class="read-more" href="{{post.url}}">&raquo;</a></p>#}
  </section>
  <footer class="post-meta">
    <time class="post-date" datetime="{{post.date | date('D MMMM YYYY')}}">{{post.date | date('D MMMM YYYY')}}</time>
    &middot;
    <span>{% for tag in post.tags %}
      <a href="/tag/{{tag}}/">{{tag}}</a>
    {% endfor%}</span>
  </footer>
</article>
{% endfor %}

simple/_themes/one/templates/page.html:
{% extends 'base.html' %}

{% block body_class %}post-template{% endblock%}

{% block content %}

<article class="post">

  <header class="post-header">
    <h1 class="post-title">{{file.title}}</h1>
  </header>

  <section class="post-content">
    {{file.content | safe}}
  </section>

</article>

{% endblock%}

simple/_themes/one/templates/tag.html:
{% extends 'base.html' %}

{% block head_title %}{{pagination.metadata}}{% endblock %}

{% block body_class %}tag-template {% if pagination.num > 1 %}archive-template{% endif %}{% endblock%}

{% block header_title %}{{pagination.metadata}}{% endblock %}
{% block header_description %}A {{pagination.total}}-post collection{% endblock %}

{% block content %}

{% include '_loop.html' %}

{% endblock%}

simple/_themes/one/templates/_pagination:
{% if pagination %}
<nav class="pagination" role="navigation">
  {% if pagination.prev %}
    <a class="newer-posts" href="{{pagination.prev_link}}">&larr; Newer Posts</a>
  {% endif %}

  <span class="page-number">{{pagination.page}} of {{pagination.total_pages}}</span>

  {% if pagination.next %}
    <a class="older-posts" href="{{pagination.next_link}}">Older Posts &rarr;</a>
  {% endif %}
</nav>
{% endif %}

simple/_themes/one/templates/index.html:
{% extends 'base.html' %}

{% block body_class %}{% if pagination.page > 1 %}archive-template paged{% else %}home-template{% endif %}{% endblock%}

{% block content %}

{% include '_loop.html' %}

{% endblock%}

simple/_themes/one/templates/post.html:
{% extends 'base.html' %}

{% block body_class %}post-template{% endblock%}

{% block content %}

<article class="post">

  <header class="post-header">
    <h1 class="post-title">{{file.title}}</h1>
    <section class="post-meta">
      <time class="post-date" datetime="{{post.date | date('D MMMM YYYY')}}">{{post.date | date('D MMMM YYYY')}}</time>
      <span>&middot; {% for tag in file.tags %}
        <a href="/tag/{{tag}}/">{{tag}}</a>
      {% endfor%}</span>
    </section>
  </header>

  <section class="post-content">
    {{file.content | safe}}
  </section>

  <footer class="post-footer">
  </footer>

</article>

{% endblock%}
